mapper.insert:
INSERT INTO grade(
  grade,
  students_info_id,
  teachers_course_id
)VALUE(
  #{grade},
  #{students_info_id},
  #{teachers_course_id}
)

mapper.update:
UPDATE grade


        grade = #{grade}


        students_info_id = #{students_info_id}


        teachers_course_id = #{teachers_course_id}


WHERE id = #{id}

mapper.select:
SELECT *
FROM grade


        id = #{id}


        and grade = #{grade}


        and students_info_id = #{students_info_id}


        and teachers_course_id = #{teachers_course_id}
