mapper.insert:
INSERT INTO grade(
  grade,
  students_info_id,
  teachers_course_id
)VALUE(
  #{grade},
  #{studentsInfoId},
  #{teachersCourseId}
)

mapper.update:
UPDATE grade


        grade = #{grade}


        students_info_id = #{studentsInfoId}


        teachers_course_id = #{teachersCourseId}


WHERE id = #{id}

mapper.select:
SELECT *
FROM grade


        id = #{id}


        and grade = #{grade}


        and students_info_id = #{studentsInfoId}


        and teachers_course_id = #{teachersCourseId}
